#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#include "fs.h"

int main(int argc, char *argv[])
{
	/* Mount disk */


    fs_mount("test.fs");
    fs_create("test-file-1");
    int fd = fs_open("test-file-1");
    fs_write(fd, "",4096);
    fs_umount();
    fs_mount("test.fs");
    fs_open("test-file-1");
    fs_lseek(fd, 0);
    char *buf = malloc(4096);
    fs_read(fd, buf, 4096);

    printf("read_buf: %s\n", buf);



	return 0;
}
